    private fun validatePassword(password: String?): Boolean {
        return password != null && password.isNotEmpty()
    }


    private fun showProgress(isInProgress: Boolean) {
        btnLogin.visibility = if (isInProgress) GONE else VISIBLE
        linkSignup.visibility = if (isInProgress) GONE else VISIBLE
        progressBar.visibility = if (isInProgress) VISIBLE else GONE
    }


    // Lifecycle stuff
    private val lifecycleRegistry = LifecycleRegistry(this)

    override fun getLifecycle(): LifecycleRegistry? {
        return lifecycleRegistry
    }
}
